import 'isomorphic-fetch'
import Link from 'next/link'

export default class extends React.Component{

    static async getInitialProps({query}){
        let idPodcast = query.id
        let req = await fetch(`https://api.audioboom.com/audio_clips/${idPodcast}`)
        let {body : audio_clip} = await req.json()

        return {audio_clip}
    }

    render(){
        const {audio_clip} = this.props

        return <div>
            <header>Podcast</header>

            <h1>{audio_clip.title}</h1>

            <div className='modal'>
                <div className='clip'>
                    <nav>
                    <Link href={`/channel?id=${"id channel"}`}>
                        <a className='close'>&lt; Volver</a>
                    </Link>
                    </nav>

                    <picture>
                    {/* Hint: Puede estar en clip.urls.image o en clip.channel.urls.logo_image.original */}
                    <div style={{ backgroundImage: `url(${"SOURCE DE LA IMAGEN"})` }} />
                    </picture>

                    <div className='player'>
                    <h3>TITLE DEL CLIP</h3>
                    <h6>{audio_clip.title}</h6>
                    <audio controls autoPlay={true}>
                        <source src={"SOURCE DEL MP3"} type='audio/mpeg' />
                    </audio>
                    </div>
                </div>
            </div>
            <style jsx global>{`
                nav {
                    background: none;
                }
                nav a {
                    display: inline-block;
                    padding: 15px;
                    color: white;
                    cursor: pointer;
                    text-decoration: none;
                }
                .clip {
                    display: flex;
                    height: 100%;
                    flex-direction: column;
                    background: #8756ca;
                    color: white;
                }
                picture {
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    flex: 1 1;
                    flex-direction: column;
                    width: auto;
                    padding: 10%;
                }
                picture div {
                    width: 100%;
                    height: 100%;
                    background-position: 50% 50%;
                    background-size: contain;
                    background-repeat: no-repeat;
                }
                .player {
                    padding: 30px;
                    background: rgba(0,0,0,0.3);
                    text-align: center;
                }
                h3 {
                    margin: 0;
                }
                h6 {
                    margin: 0;
                    margin-top: 1em;
                }
                audio {
                    margin-top: 2em;
                    width: 100%;
                }
                .modal {
                    position: fixed;
                    top: 0;
                    left: 0;
                    right: 0;
                    bottom: 0;
                    z-index: 99999;
                }
            `}</style>
            <style jsx global>{`
            body {
                margin: 0;
                font-family: system-ui;
                background: white;
            }
            `}</style>
        </div>
    }
}
